import React, { useState } from "react";
import { Row, Col, Container, Button, Modal, Tab, Tabs } from "react-bootstrap";

const Classes = (classes) => {
  const classArray = classes.classes;

  const [showModal, setShowModal] = useState(false);
  const [classTitle, setClassTitle] = useState("");

  console.log(classTitle);

  return (
    <>
      <Container className="classes text-center" fluid>
        <Row>
          <Col>
            <h3 style={{ color: "white", textAlign: "left" }}>Your Classes</h3>
          </Col>
        </Row>
        <Row>
          {classArray.map((result) => {
            return (
              <Col
                xl={4}
                lg={4}
                md={4}
                sm={6}
                xs={6}
                key={result.properties.title}
              >
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "#535353",
                    marginBottom: "20px",
                    width: "100%",
                    height: "10vh",
                    fontSize: "calc(10px + 1.2vw)",
                    fontWeight: "bold",
                    borderRadius: "10px",
                  }}
                  id={result.properties.title}
                  onClick={(e) => {
                    setShowModal(true);
                    setClassTitle(e.target.id);
                  }}
                >
                  {result.properties.title}
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
      <Modal
        className="text-center"
        size="lg"
        show={showModal}
        onHide={() => setShowModal(false)}
      >
        <Modal.Header closeButton>
          <Modal.Title style={{ fisplay: "flex" }}>
            <h3 style={{ fontWeight: "bold", color: "#535353" }}>
              {classTitle}
            </h3>
            <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
              <Tab title="Attendance"></Tab>
              <Tab title="Students"></Tab>
            </Tabs>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body></Modal.Body>
      </Modal>
    </>
  );
};

export default Classes;
