import React, { useEffect, useState } from "react";
import { Row, Col, Container } from "react-bootstrap";
import NavBar from "./NavBar";
import LatestAttendance from "./LatestAttendance";
import StudentMatters from "./StudentMatters";
import Classes from "./Classes";

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [attendance, setAttendance] = useState([]);

  const getData = async () => {
    let classArray = [];
    let studentArray = [];
    let dataArray = [];
    let attendance = [];

    await fetch("/drive/api");

    await fetch("/sheet/api")
      .then((res) => res.json())
      .then((res) => {
        setClasses(res.sheets);
        res.sheets.forEach((result) => {
          classArray.push(result.properties.title);
        });
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <NavBar />
      <Container className="text-center dashboard" fluid>
        <Row>
          <Col>
            <h3
              style={{
                fontSize: "calc(22px + 2vw)",
                fontWeight: "bold",
                color: "#535353",
                textAlign: "left",
              }}
            >
              Your Dashboard
            </h3>
          </Col>
        </Row>

        <Row>
          <Col xl={6} lg={12} md={12} sm={12} xs={12}>
            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <LatestAttendance />
              </Col>
            </Row>

            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <StudentMatters />
              </Col>
            </Row>
          </Col>

          <Col
            xl={6}
            lg={12}
            md={12}
            sm={12}
            xs={12}
            style={{ marginBottom: "10px" }}
          >
            <Classes classes={classes} />
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
