import React, { useState } from "react";
import axios from "axios";
import { Row, Col, Container, Button, Modal, Spinner } from "react-bootstrap";

const LatestAttendance = (attendance) => {
  const attendanceArray = attendance.attendance;

  const [showModal, setShowModal] = useState(false);
  const [classTitle, setClassTitle] = useState("");
  const [date, setDate] = useState("");

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          {attendanceArray.map((result) => {
            return (
              <Col xl={4} lg={4} md={4} sm={6} xs={6} key={result.Class}>
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "#535353",
                    marginBottom: "20px",
                    height: "15vh",
                    fontSize: "calc(1rem + 0.8vw)",
                    fontWeight: "bold",
                    borderRadius: "10px",
                  }}
                  id={result.Class}
                  value={result.Date}
                  onClick={(e) => console.log(e.target.id)}
                >
                  <div
                    style={{ marginBottom: "5px" }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => console.log(e.target.id)}
                  >
                    {result.Class}
                  </div>

                  <div
                    style={{
                      fontSize: "calc(1rem + 0.5vw)",
                      color: "white",
                      backgroundColor: "#3e4ccd",
                      borderRadius: "10px",
                      marginBottom: "5px",
                    }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => console.log(e.currentTarget.value)}
                  >
                    {result.Present} / {result.Total_Students}
                  </div>

                  <div
                    style={{ fontSize: "calc(1rem + 0.5vw)" }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => console.log(e.target.id)}
                  >
                    {result.Date}
                  </div>
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
