import React, { useState, useContext, useEffect } from "react";
import { AccountContext } from "./Account";

const Settings = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  const { getSession } = useContext(AccountContext);

  useEffect(() => {
    getSession().then(() => {
      setLoggedIn(true);
    });
  });

  return (
    <div>
      {loggedIn && (
        <>
          <h2>Settings</h2>
        </>
      )}
    </div>
  );
};

export default Settings;
