import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  Container,
  ListGroup,
  Spinner,
  Button,
  Form,
} from "react-bootstrap";
import CsvDownload from "react-json-to-csv";

const AttendanceList = () => {
  const [attendanceArray, setAttendanceArray] = useState([]);
  const [attendanceList, setAttendanceList] = useState([]);
  const [dates, setDates] = useState([]);
  const [showAttendance, setShowAttendance] = useState(false);
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedClass, setSelectedClass] = useState("");
  const [studentList, setstudentList] = useState([]);
  const [absentList, setAbsentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [editStatus, setEditStatus] = useState(false);

  useEffect(() => {
    let mounted = true;
    let dateArray = [];
    let studentList = [];

    const getData = async () => {
      const response = await fetch("/aws/api").then((res) => res.json()); // Get Student Attendance
      const studentArray = await fetch("/sheet/student").then((res) =>
        res.json()
      ); // Get Student List

      // Convert array to object
      studentArray.forEach((res) => {
        studentList.push({
          Name: res[0],
          ID: res[1],
        });
      });

      response.sort((a, b) => {
        // Sort to latest date
        return new Date(b.Date) - new Date(a.Date);
      });

      response.forEach((data) => {
        dateArray.push(data.Date.split(" ")[0]);
      });

      dateArray = [...new Set(dateArray)]; // Remove Duplicates

      if (mounted) {
        setAttendanceArray(response);
        setDates(dateArray);
        setstudentList(studentList);
        setIsLoading(false);
      }
    };
    getData();
    return () => {
      mounted = false;
    };
  }, []);

  const getAttendanceList = (date) => {
    let absentList;
    const selectedDate = date;
    const attendance = attendanceArray.filter(
      (
        res // Filter Attendance By Selected Date
      ) => res.Date.includes(selectedDate)
    );

    // Filter absent students
    absentList = studentList.filter(
      ({ Name: id1 }) => !attendance.some(({ Name: id2 }) => id2 === id1)
    );

    setSelectedClass(attendance[0].Class);
    setSelectedDate(date);
    setAttendanceList(attendance);
    setAbsentList(absentList);
    setShowAttendance(true);
  };

  return (
    <>
      <Container className="text-center attendancelist">
        {showAttendance ? (
          <>
            <Row>
              <Col>
                <h3
                  style={{
                    fontWeight: "bold",
                    color: "white",
                    textAlign: "left",
                    marginTop: "10px",
                    marginBottom: "10px",
                  }}
                >
                  {selectedDate}
                </h3>
              </Col>
            </Row>
            <Row>
              <Col>
                <ListGroup>
                  <h3 style={{ color: "white" }}>Attended</h3>
                  {attendanceList.map((student) => {
                    return (
                      <ListGroup.Item key={student.Name}>
                        <Row>
                          <Col
                            xl={6}
                            lg={6}
                            md={6}
                            sm={6}
                            xs={6}
                            style={{
                              fontWeight: "bold",
                              fontSize: "calc(0.5rem + 0.6vw)",
                              color: "#535353",
                              textAlign: "left",
                            }}
                          >
                            {student.Name}
                          </Col>
                          <Col
                            xl={3}
                            lg={3}
                            md={3}
                            sm={3}
                            xs={3}
                            style={{
                              fontWeight: "bold",
                              fontSize: "calc(0.5rem + 0.6vw)",
                              color: "#535353",
                              textAlign: "right",
                            }}
                          >
                            {student.Temperature} Â°C
                          </Col>
                          {editStatus === true && (
                            <Col xl={3} lg={3} md={3} sm={3} xs={3}>
                              <Form.Check type="checkbox" defaultChecked />
                            </Col>
                          )}
                        </Row>
                      </ListGroup.Item>
                    );
                  })}
                  <h3 style={{ color: "white", marginTop: "5px" }}>Absent</h3>
                  {absentList.map((student) => {
                    return (
                      <ListGroup.Item key={student.Name}>
                        <Row>
                          <Col
                            xl={6}
                            lg={6}
                            md={6}
                            sm={6}
                            xs={6}
                            style={{
                              fontWeight: "bold",
                              fontSize: "calc(0.5rem + 0.6vw)",
                              color: "#535353",
                              textAlign: "left",
                            }}
                          >
                            {student.Name}
                          </Col>
                          <Col
                            xl={3}
                            lg={3}
                            md={3}
                            sm={3}
                            xs={3}
                            style={{
                              fontWeight: "bold",
                              fontSize: "calc(0.5rem + 0.6vw)",
                              color: "#535353",
                              textAlign: "right",
                            }}
                          >
                            {student.ID}
                          </Col>
                          {editStatus === true && (
                            <Col xl={3} lg={3} md={3} sm={3} xs={3}>
                              <Form.Check type="checkbox" />
                            </Col>
                          )}
                        </Row>
                      </ListGroup.Item>
                    );
                  })}
                </ListGroup>
              </Col>
            </Row>
            <Row>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                  }}
                  onClick={() => setShowAttendance(false)}
                >
                  Back
                </Button>
              </Col>
              <Col>
                <CsvDownload
                  data={attendanceList}
                  filename={selectedDate + " " + selectedClass + ".csv"}
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                    borderRadius: "5px",
                    borderColor: "#007bff",
                    padding: ".375rem .75rem",
                  }}
                >
                  Download
                </CsvDownload>
              </Col>
            </Row>
          </>
        ) : (
          <>
            <Row>
              <Col>
                <h3
                  style={{
                    fontWeight: "bold",
                    color: "white",
                    textAlign: "left",
                    marginTop: "10px",
                    marginBottom: "10px",
                  }}
                >
                  Attendance List
                </h3>
              </Col>
            </Row>
            <Row>
              <Col>
                {isLoading ? (
                  <Spinner animation="border" style={{ color: "white" }} />
                ) : (
                  <>
                    {dates.length === 0 && (
                      <h3 style={{ color: "white" }}>No Data</h3>
                    )}
                    {dates.length > 0 && (
                      <ListGroup>
                        {dates.map((date) => {
                          return (
                            <ListGroup.Item
                              key={date}
                              id={date}
                              action
                              onClick={(e) => getAttendanceList(e.target.id)}
                              style={{ fontWeight: "bold", color: "#535353" }}
                            >
                              {date}
                            </ListGroup.Item>
                          );
                        })}
                      </ListGroup>
                    )}
                  </>
                )}
              </Col>
            </Row>
          </>
        )}
      </Container>
    </>
  );
};

export default AttendanceList;
