import React, { useContext, useState } from "react";
import { Button, Navbar, Nav, Spinner } from "react-bootstrap";
import { AccountContext } from "../Authentication/Account";

const NavBar = () => {
  const { logout, userEmail } = useContext(AccountContext);
  const [isLoading, setIsLoading] = useState(false);

  if (isLoading) {
    setTimeout(() => {
      logout();
    }, 1500);
  }

  return (
    <>
      <Navbar className="navbar" collapseOnSelect expand="lg">
        <Navbar.Brand style={{ display: "flex" }}>
          <h3
            style={{
              fontSize: "30px",
              fontWeight: "bold",
              color: "white",
            }}
          >
            RASPITAR
          </h3>
          <h3
            style={{
              textDecoration: "underline",
              marginLeft: "10px",
              fontSize: "40px",
              fontWeight: "bold",
              color: "white",
            }}
          >
            WEB
          </h3>
        </Navbar.Brand>
        <Navbar.Toggle
          aria-controls="responsive-navbar-nav"
          style={{ backgroundColor: "white" }}
        />
        <Navbar.Collapse
          className="justify-content-end"
          id="responsive-navbar-nav"
        >
          <Nav>
            <Navbar.Text
              style={{
                marginRight: "10px",
                marginBottom: "10px",
                color: "white",
                fontSize: "18px",
              }}
            >
              Signed in as: {userEmail}
            </Navbar.Text>
            <Button
              style={{
                marginRight: "10px",
                marginBottom: "10px",
                backgroundColor: "white",
                color: "black",
                fontWeight: "bold",
                fontSize: "18px",
              }}
            >
              Change Password
            </Button>

            <Button
              style={{
                marginRight: "10px",
                marginBottom: "10px",
                backgroundColor: "white",
                color: "black",
                fontWeight: "bold",
                fontSize: "18px",
              }}
              onClick={() => {
                setIsLoading(true);
              }}
            >
              {isLoading ? (
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                />
              ) : (
                <span>Log Out</span>
              )}
            </Button>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </>
  );
};

export default NavBar;
