import React, { useContext, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
import axios from "axios";

import NavBar from "./NavBar";
import LatestAttendance from "./LatestAttendance";
import StudentMatters from "./StudentMatters";
import Classes from "./Classes";

import { DataContext } from "./Data";
import Test from "./test";

const Dashboard = () => {
  //const { handleClientLoad, getStudents, classArray } = useContext(DataContext);

  useEffect(() => {
    fetch("/drive/api")
      .then((res) => res.json())
      .then((res) => Object.assign({}, res.files[0]))
      .then((data) => {
        axios.post("/sheet/id", data);
      });
  });

  return (
    <>
      <Test />
      <NavBar />
      <Container className="text-center dashboard" fluid>
        <Row>
          <Col>
            <h3
              style={{
                fontSize: "calc(22px + 2vw)",
                fontWeight: "bold",
                color: "#535353",
                textAlign: "left",
              }}
            >
              Your Dashboard
            </h3>
          </Col>
        </Row>

        <Row>
          <Col xl={6} lg={12} md={12} sm={12} xs={12}>
            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <LatestAttendance />
              </Col>
            </Row>

            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <StudentMatters />
              </Col>
            </Row>
          </Col>

          <Col
            xl={6}
            lg={12}
            md={12}
            sm={12}
            xs={12}
            style={{ marginBottom: "10px" }}
          >
            <Classes />
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
