import React, { useContext, useState, useEffect } from "react";
import { AccountContext } from "../Authentication/Account";
import { Row, Col, Container, Jumbotron, Button } from "react-bootstrap";
import gapi from "gapi-client";
import "../../credentials.json";

const LatestAttendance = () => {
  const { userEmail } = useContext(AccountContext);
  const [data, setData] = useState([]);

  const handleSheetClientLoad = () => {
    gapi.load("client:auth2", initSheetClient);
  };

  const handleDriveClientLoad = () => {
    gapi.load("client:auth2", initDriveClient);
    gapi.auth2.getAuthInstance().signIn();
  };

  const initSheetClient = () => {
    var CLIENT_ID =
      "714844314792-7qpq28p5nb943776gms8rin859iodaua.apps.googleusercontent.com";
    var API_KEY = "AIzaSyDFjwcJ8OuJP24_WCn-bxX1LsJMCSvBsl8";
    var DISCOVERY_DOCS = [
      "https://sheets.googleapis.com/$discovery/rest?version=v4",
    ];
    var SCOPES = "https://www.googleapis.com/auth/spreadsheets.readonly";

    gapi.client
      .init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: DISCOVERY_DOCS,
        scope: SCOPES,
      })
      .then(() => {
        getData();
      });
  };

  const initDriveClient = () => {
    var CLIENT_ID =
      "714844314792-7qpq28p5nb943776gms8rin859iodaua.apps.googleusercontent.com";
    var API_KEY = "AIzaSyDFjwcJ8OuJP24_WCn-bxX1LsJMCSvBsl8";
    var DISCOVERY_DOCS = [
      "https://www.googleapis.com/discovery/v1/apis/drive/v3/rest",
    ];
    var SCOPES = "https://www.googleapis.com/auth/drive.metadata.readonly";

    gapi.client
      .init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: DISCOVERY_DOCS,
        scope: SCOPES,
      })
      .then(() => {
        getData();
      });
  };

  const getData = () => {
    // GET VALUES FROM GOOGLE SHEET
    /*gapi.client.sheets.spreadsheets.values
      .get({
        spreadsheetId: "1mPbAIEBiixpfc73FiHcqZVYRaIeV_onnt2qIzBGqViQ",
        range: "DTLE-D22",
      })
      .then((response) => {
        console.log(response.result.values);
      });*/

    gapi.client.drive.files
      .list({
        pageSize: 1000,
        q:
          "mimeType = 'application/vnd.google-apps.folder' and trashed = false",
        fields: "nextPageToken, files(id, name)",
      })
      .then(function (response) {
        console.log(response.result);
      });
  };

  useEffect(() => {
    handleDriveClientLoad();
    //handleSheetClientLoad();
  }, []);

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
