import React, { useState, useEffect } from "react";
import { Row, Col, Container, Button } from "react-bootstrap";

const LatestAttendance = () => {
  const [classes, setClasses] = useState([])
  const [data, setData] = useState([])
  const [students, setStudents] = useState([])
  const [attendance, setAttendance] = useState({Class: "", Date: "", Total: "", Present: ""})

  const getData = async () => {
    let classArray = []

    await fetch("/drive/api")
    
    await fetch("/sheet/api") // Get Class List
      .then((res) => res.json())
      .then((res) => {
        res.sheets.forEach((result) => {
          classArray.push(result.properties.title)
        })
      });

    await fetch("/sheet/student") // Get Student List
      .then((res) => res.json())
      .then((res) => {
        setStudents(res)
      });

    await fetch("/aws/api") // Get Student Attendance
      .then((res) => res.json())
      .then((res) => setData(res))

      setClasses(classArray)
  };

  useEffect(() => {
    getData();
  }, []);

  // Sort to latest date
  data.sort((a, b) => {
    return new Date(b.Date) - new Date(a.Date); 
  })

  const latestAttendanceData = data.filter((res) => res.Date.includes(data[0].Date.split(" ")[0]))

  let totalStudents = students.length;
  let presentCount = 0;

  students.forEach((res) => {
    latestAttendanceData.forEach((student) => {
      if(res[0].includes(student.Name)){
        presentCount =  presentCount + 1
      }
    })
  })

  let absenceCount = totalStudents - presentCount;
  
  // Class, Date, Number 
  console.log(attendance)
