import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  Container,
  ListGroup,
  Spinner,
  Button,
} from "react-bootstrap";

const AttendanceList = () => {
  const [attendanceArray, setAttendanceArray] = useState([]);
  const [attendanceList, setAttendanceList] = useState([]);
  const [dates, setDates] = useState([]);
  const [showAttendance, setShowAttendance] = useState(false);
  const [selectedDate, setSelectedDate] = useState("");
  const [studentList, setStudentList] = useState([]);
  const [absent, setAbsent] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    let dateArray = [];

    const getData = async () => {
      const studentArray = await fetch("/sheet/student").then((res) =>
        res.json()
      ); // Get Student List
      const response = await fetch("/aws/api").then((res) => res.json()); // Get Student Attendance

      response.sort((a, b) => {
        // Sort to latest date
        return new Date(b.Date) - new Date(a.Date);
      });

      response.forEach((data) => {
        dateArray.push(data.Date.split(" ")[0]);
      });

      dateArray = [...new Set(dateArray)]; // Remove Duplicates

      if (mounted) {
        setAttendanceArray(response);
        setDates(dateArray);
        setStudentList(studentArray);
        setIsLoading(false);
      }
    };
    getData();
    return () => {
      mounted = false;
    };
  }, []);

  const getAttendanceList = (date) => {
    let absent = [];
    const selectedDate = date;
    const attendance = attendanceArray.filter((
      res // Filter Attendance By Selected Date
    ) => res.Date.includes(selectedDate));

    console.log("Attendance: ", attendance);
    console.log("Students: ", studentList);

    studentList.forEach((res) => {
      // Filter Absent Students
      attendance.forEach((student) => {
        if (res[0] !== student.Name) {
          console.log(student.Name);
        }
      });
    });

    setSelectedDate(date);
    setAttendanceList(attendance);
    setShowAttendance(true);
  };

  return (
    <>
      <Container className="text-center attendancelist">
        {showAttendance ? (
          <>
            <Row>
              <Col>
                <h3
                  style={{
                    fontWeight: "bold",
                    color: "white",
                    textAlign: "left",
                    marginTop: "10px",
                    marginBottom: "10px",
                  }}
                >
                  {selectedDate}
                </h3>
              </Col>
            </Row>
            <Row>
              <Col>
                <ListGroup>
                  {attendanceList.map((student) => {
                    return (
                      <ListGroup.Item key={student.Name}>
                        <Row>
                          <Col
                            xl={8}
                            lg={8}
                            md={8}
                            sm={8}
                            xs={8}
                            style={{
                              fontWeight: "bold",
                              color: "#535353",
                              textAlign: "left",
                            }}
                          >
                            {student.Name}
                          </Col>
                          <Col
                            xl={4}
                            lg={4}
                            md={4}
                            sm={4}
                            xs={4}
                            style={{
                              fontWeight: "bold",
                              color: "#535353",
                              textAlign: "right",
                            }}
                          >
                            {student.Temperature} Â°C
                          </Col>
                        </Row>
                      </ListGroup.Item>
                    );
                  })}
                </ListGroup>
              </Col>
            </Row>
            <Row>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                  }}
                  onClick={() => setShowAttendance(false)}
                >
                  Back
                </Button>
              </Col>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                  }}
                >
                  Download
                </Button>
              </Col>
            </Row>
          </>
        ) : (
          <>
            <Row>
              <Col>
                <h3
                  style={{
                    fontWeight: "bold",
                    color: "white",
                    textAlign: "left",
                    marginTop: "10px",
                    marginBottom: "10px",
                  }}
                >
                  Attendance List
                </h3>
              </Col>
            </Row>
            <Row>
              <Col>
                {isLoading ? (
                  <Spinner animation="border" style={{ color: "white" }} />
                ) : (
                  <ListGroup>
                    {dates.map((date) => {
                      return (
                        <ListGroup.Item
                          key={date}
                          id={date}
                          action
                          onClick={(e) => getAttendanceList(e.target.id)}
                          style={{ fontWeight: "bold", color: "#535353" }}
                        >
                          {date}
                        </ListGroup.Item>
                      );
                    })}
                  </ListGroup>
                )}
              </Col>
            </Row>
          </>
        )}
      </Container>
    </>
  );
};

export default AttendanceList;
