import React from "react";
import { Row, Col, Container, Toast, Spinner } from "react-bootstrap";

const StudentMatters = (props) => {
  const temperatureMatterArray = props.temperatureMatter;
  const isLoading = props.isLoading;

  return (
    <Container className="student-matters text-center" fluid>
      <h3 style={{ color: "white", textAlign: "left" }}>Student Matters</h3>
      <Row style={{ marginBottom: "10px" }}>
        <Col xl={6} lg={6} md={6} sm={12} xs={12}>
          <h3 style={{ color: "white", fontSize: "22px", textAlign: "left" }}>
            High Temperature
          </h3>

          {isLoading ? (
            <Row>
              <Col>
                <Spinner
                  animation="border"
                  style={{ color: "white", marginBottom: "20px" }}
                />
              </Col>
            </Row>
          ) : (
            <Row style={{ marginBottom: "10px" }}>
              {temperatureMatterArray.map((result) => {
                return (
                  <Col
                    xl={12}
                    lg={12}
                    md={12}
                    sm={12}
                    xs={12}
                    key={result.Name}
                  >
                    <Toast style={{ marginBottom: "5px" }}>
                      <Toast.Header>
                        <strong className="mr-auto">{result.Name}</strong>
                        <small style={{ fontWeight: "bold" }}>
                          {result.Class}
                        </small>
                      </Toast.Header>
                      <Toast.Body
                        style={{ color: "#535353", fontWeight: "bold" }}
                      >
                        {result.Temperature} Â°C &emsp; {result.Date}
                      </Toast.Body>
                    </Toast>
                  </Col>
                );
              })}
            </Row>
          )}
        </Col>

        <Col xl={6} lg={6} md={6} sm={12} xs={12}>
          <h3 style={{ color: "white", fontSize: "22px", textAlign: "left" }}>
            Low Attendance
          </h3>
          {isLoading ? (
            <Row>
              <Col>
                <Spinner
                  animation="border"
                  style={{ color: "white", marginBottom: "20px" }}
                />
              </Col>
            </Row>
          ) : (
            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <h3>Test</h3>
              </Col>
            </Row>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default StudentMatters;
