import React, { useEffect, useState } from "react";
import axios from "axios";
import { Row, Col, Container, ListGroup } from "react-bootstrap";

const StudentList = (classTitle) => {
  const className = classTitle.classTitle;
  const [studentList, setStudentList] = useState([]);

  const getData = async () => {
    let studentArray = [];
    await fetch("/drive/api");

    axios.post("http://localhost:3001/sheet/class", className);

    await fetch("/sheet/student") // Get Student List
      .then((res) => res.json())
      .then((res) => (studentArray = res));

    setStudentList(studentArray);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container>
        <Row>
          <Col>Student Lists</Col>
        </Row>
        <Row>
          <Col>
            <ListGroup>
              {studentList.map((student) => {
                return (
                  <ListGroup.Item key={student[1]}>{student[0]}</ListGroup.Item>
                );
              })}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default StudentList;
