import React, { useEffect, useState } from "react";
import axios from "axios";
import { Row, Col, Container, Jumbotron, Button } from "react-bootstrap";

const Classes = () => {
  const [data, setData] = useState([]);

  const getData = async () => {
    await fetch("/drive/api")
      .then((res) => res.json())
      .then((res) => Object.assign({}, res.files[0]))
      .then((data) => {
        axios.post("/sheet/id", data);
      });

    await fetch("/sheet/api")
      .then((res) => res.json())
      .then((res) => setData(res.sheets));
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="classes text-center" fluid>
        <Row>
          <Col>
            <h3 style={{ color: "white", textAlign: "left" }}>Your Classes</h3>
          </Col>
        </Row>
        <Row>
          {data.map((result) => {
            return (
              <Col xl={4} lg={4} md={4} sm={6} xs={6}>
                <Button
                  style={{
                    backgroundColor: "white",
                    width: "auto",
                    height: "auto",
                  }}
                >
                  <h3 style={{ fontSize: "calc(10px + 1vw)" }}>
                    {result.properties.title}
                  </h3>
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
};

export default Classes;
