import React, { useState, useEffect } from "react";
import axios from "axios";
import { Row, Col, Container, Button } from "react-bootstrap";

const LatestAttendance = () => {
  const [data, setData] = useState([]);
  const [classData, setClassData] = useState([])

  const getData = async () => {
    /*await fetch("/aws/api")
      .then((res) => res.json())
      .then((res) => setData(res));*/

    await fetch("/drive/api")
      .then((res) => res.json())
      .then((res) => Object.assign({}, res.files[0]))
      .then((data) => {
        axios.post("/sheet/id", data);
      });

    await fetch("/sheet/api")
      .then((res) => res.json())
      .then((res) => res.sheets.map((result) => {
        setClassData(result.properties.title)
      }));

    await fetch("/sheet/student")
      .then((res) => res.json())
      .then((res) => setData(res));
  };

  useEffect(() => {
    getData();
  }, []);

  console.log(classData)
  
  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
        
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
