import React, { useContext, useState, useEffect } from "react";
import { AccountContext } from "../Authentication/Account";
import { Row, Col, Container, Jumbotron, Button } from "react-bootstrap";
import gapi from "gapi-client";
import "../../credentials.json";
import Classes from "./Classes";

const LatestAttendance = () => {
  const { userEmail } = useContext(AccountContext);
  const [classArray, setClassArray] = useState([]);
  const [sheetID, setSheetID] = useState("");

  const handleClientLoad = () => {
    gapi.load("client:auth2", initClient);
  };

  const initClient = () => {
    var CLIENT_ID =
      "714844314792-7qpq28p5nb943776gms8rin859iodaua.apps.googleusercontent.com";
    var API_KEY = "AIzaSyDFjwcJ8OuJP24_WCn-bxX1LsJMCSvBsl8";

    var DISCOVERY_DOCS = [
      "https://www.googleapis.com/discovery/v1/apis/drive/v3/rest",
      "https://sheets.googleapis.com/$discovery/rest?version=v4",
    ];
    var SCOPES =
      "https://www.googleapis.com/auth/drive.metadata.readonly https://www.googleapis.com/auth/spreadsheets.readonly";

    gapi.client
      .init({
        apiKey: API_KEY,
        clientId: CLIENT_ID,
        discoveryDocs: DISCOVERY_DOCS,
        scope: SCOPES,
      })
      .then(() => {
        //gapi.auth2.getAuthInstance().signIn();
        getClasses();
      });
  };

  const getClasses = () => {
    var sheetID;

    // GET SPECIFIC SPREADSHEETID FROM GOOGLE DRIVE
    gapi.client.drive.files
      .list({
        q: "name = " + "'" + userEmail + "'", // eslint-disable-line prefer-template
      })
      .then((response) => {
        sheetID = response.result.files[0].id;
        setSheetID(sheetID);
        // GET CLASSES FROM GOOGLE SHEET
        gapi.client.sheets.spreadsheets
          .get({
            spreadsheetId: sheetID,
          })
          .then((response) => {
            setClassArray(response.result.sheets);
          });
      });
  };

  const getStudents = () => {
    // GET STUDENTS FROM EACH CLASS
    classArray.forEach((className) => {
      console.log(className.properties.title);
    });

    /*gapi.client.sheets.spreadsheets
      .get({
        spreadsheetId: sheetID,
        //range: "CE-D21",
      })
      .then((response) => {
        setClasses(response.result.sheets);
        //console.log(" Students: ", response.result.values);
      });*/
  };

  useEffect(() => {
    handleClientLoad();
    getStudents();
  }, []);

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
