import React, { useState } from "react";
import axios from "axios";
import { Row, Col, Container, Button, Modal } from "react-bootstrap";
import SelectedAttendance from "./SelectedAttendance";
import StudentList from "./StudentList";

const LatestAttendance = (attendance) => {
  const attendanceArray = attendance.attendance;

  const [showModal, setShowModal] = useState(false);
  const [classTitle, setClassTitle] = useState("");
  const [date, setDate] = useState("");
  const [option, setOption] = useState("");

  const selectHandler = (e) => {
    axios.post("http://localhost:3001/sheet/class", e.target.id);

    setShowModal(true);
    setDate(e.currentTarget.value);
    setClassTitle(e.target.id);
  };

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          {attendanceArray.map((result) => {
            return (
              <Col xl={4} lg={4} md={4} sm={6} xs={6} key={result.Class}>
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "#535353",
                    marginBottom: "20px",
                    height: "15vh",
                    fontSize: "calc(1rem + 0.8vw)",
                    fontWeight: "bold",
                    borderRadius: "10px",
                  }}
                  id={result.Class}
                  value={result.Date}
                  onClick={(e) => {
                    selectHandler(e);
                  }}
                >
                  {result.Class}
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
      <Modal
        className="text-center"
        size="lg"
        show={showModal}
        onHide={() => {
          setShowModal(false);
        }}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h3 style={{ fontWeight: "bold", color: "#535353" }}>
              {classTitle}
            </h3>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Col>
              <Button
                xl={6}
                lg={6}
                md={6}
                sm={6}
                xs={6}
                style={{
                  width: "100%",
                  backgroundColor: "#3e4ccd",
                  fontWeight: "bold",
                }}
                onClick={() => setOption("View")}
              >
                View
              </Button>
            </Col>
            <Col xl={6} lg={6} md={6} sm={6} xs={6}>
              <Button
                style={{
                  width: "100%",
                  backgroundColor: "#3e4ccd",
                  fontWeight: "bold",
                }}
                onClick={() => setOption("Student")}
              >
                Download
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              {option === "View" && <SelectedAttendance date={date} />}
              {option === "Student" && <StudentList />}
            </Col>
          </Row>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default LatestAttendance;
