import React, { useState, useEffect } from "react";
import axios from "axios";
import { Row, Col, Container, Button, Spinner } from "react-bootstrap";

const AttendanceList = (classTitle) => {
  const [attendanceList, setAttendanceList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const className = classTitle.classTitle;

  const getData = async () => {
    let attendanceArray = [];

    axios.post("http://localhost:3001/sheet/class", className);

    await fetch("/aws/api") // Get Student Attendance
      .then((res) => res.json())
      .then((res) => {
        attendanceArray = res;
        setIsLoading(false);
      });

    console.log(attendanceArray);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="text-center">
        <Row>
          <Col>
            <h3
              style={{
                fontWeight: "bold",
                color: "#535353",
                textAlign: "left",
                marginTop: "10px",
                marginBottom: "10px",
              }}
            >
              Attendance List
            </h3>
          </Col>
        </Row>
        <Row>
          <Col
            xl={9}
            lg={9}
            md={9}
            sm={9}
            xs={9}
            style={{
              textAlign: "left",
              fontWeight: "bold",
              color: "#535353",
            }}
          >
            {isLoading ? (
              <Spinner animation="border" style={{ color: "white" }} />
            ) : (
              <h3>Loaded</h3>
            )}
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AttendanceList;
