import React, { useState, useEffect } from "react";
import {
  Row,
  Col,
  Container,
  Spinner,
  ListGroup,
  Button,
  Form,
} from "react-bootstrap";
import CsvDownload from "react-json-to-csv";
import AWS from "aws-sdk";

const SelectedAttendance = (date) => {
  const selectedDate = date.date;

  const [isLoading, setIsLoading] = useState(true);
  const [attendanceList, setAttendanceList] = useState([]);
  const [selectedClass, setSelectedClass] = useState("");
  const [absentList, setAbsentList] = useState([]);
  const [editStatus, setEditStatus] = useState(false);
  const [removeStudents, setRemoveStudents] = useState([]);
  const [addStudents, setAddStudents] = useState([]);

  // AWS DynamoDB Config
  AWS.config.update({
    region: "ap-southeast-1",
    accessKeyId: "AKIAVT4QMHKTJR6VFAEN",
    secretAccessKey: "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",
  });

  const dynamodbClient = new AWS.DynamoDB.DocumentClient();
  const TABLE_NAME = "March";

  useEffect(() => {
    let mounted = true;
    let studentList = [];
    let absentList;

    const getData = async () => {
      const response = await fetch("/aws/api").then((res) => res.json()); // Get Student Attendance
      const studentArray = await fetch("/sheet/student").then((res) =>
        res.json()
      ); // Get Student List

      // Convert array to object
      studentArray.forEach((res) => {
        studentList.push({
          Name: res[0],
          ID: res[1],
        });
      });

      response.sort((a, b) => {
        // Sort to latest datex
        return new Date(b.Date) - new Date(a.Date);
      });

      const attendance = response.filter(
        (
          res // Filter Attendance By Selected Date
        ) => res.Date.includes(selectedDate)
      );

      // Filter absent students
      absentList = studentList.filter(
        ({ Name: id1 }) => !attendance.some(({ Name: id2 }) => id2 === id1)
      );

      if (mounted) {
        setSelectedClass(attendance[0].Class);
        setAttendanceList(attendance);
        setAbsentList(absentList);
        setIsLoading(false);
      }
    };
    getData();
    return () => {
      mounted = false;
    };
  }, [selectedDate]);

  const removeStudentHandler = (e) => {};

  const addStudentHandler = (e) => {};

  const saveHandler = () => {
    const params = {
      TableName: TABLE_NAME,
      Item: {
        "Date": "2021-05-20",
        "Name": "Test",
        "Class": "DTLE-D22",
        "Student ID": "123A",
      },
    };

    dynamodbClient.put(params, function (err, data) {
      if (err) {
        console.log(err);
      } else {
        console.log("Added item:", JSON.stringify(data, null, 2));
      }
    });
  };

  return (
    <>
      <Container className="selected-attendance text-center" fluid>
        <Row>
          <Col>
            <h3
              style={{ fontWeight: "bold", color: "white", textAlign: "left" }}
            >
              {selectedDate}
            </h3>
          </Col>
        </Row>
        <Row>
          <Col>
            {isLoading ? (
              <Spinner animation="border" style={{ color: "white" }} />
            ) : (
              <ListGroup>
                <h3 style={{ color: "white" }}>Attended</h3>
                {attendanceList.map((student) => {
                  return (
                    <ListGroup.Item key={student.Name}>
                      <Row>
                        <Col
                          xl={6}
                          lg={6}
                          md={6}
                          sm={6}
                          xs={6}
                          style={{
                            fontWeight: "bold",
                            fontSize: "calc(0.5rem + 0.6vw)",
                            color: "#535353",
                            textAlign: "left",
                          }}
                        >
                          {student.Name}
                        </Col>
                        <Col
                          xl={3}
                          lg={3}
                          md={3}
                          sm={3}
                          xs={3}
                          style={{
                            fontWeight: "bold",
                            fontSize: "calc(0.5rem + 0.6vw)",
                            color: "#535353",
                            textAlign: "right",
                          }}
                        >
                          {student.Temperature} Â°C
                        </Col>
                        {editStatus === true && (
                          <Col xl={3} lg={3} md={3} sm={3} xs={3}>
                            <Form.Check
                              type="checkbox"
                              defaultChecked
                              value={student.Name}
                              onChange={(e) => removeStudentHandler(e)}
                            />
                          </Col>
                        )}
                      </Row>
                    </ListGroup.Item>
                  );
                })}
                <h3 style={{ color: "white", marginTop: "5px" }}>Absent</h3>
                {absentList.map((student) => {
                  return (
                    <ListGroup.Item key={student.Name}>
                      <Row>
                        <Col
                          xl={6}
                          lg={6}
                          md={6}
                          sm={6}
                          xs={6}
                          style={{
                            fontWeight: "bold",
                            fontSize: "calc(0.5rem + 0.6vw)",
                            color: "#535353",
                            textAlign: "left",
                          }}
                        >
                          {student.Name}
                        </Col>
                        <Col
                          xl={3}
                          lg={3}
                          md={3}
                          sm={3}
                          xs={3}
                          style={{
                            fontWeight: "bold",
                            fontSize: "calc(0.5rem + 0.6vw)",
                            color: "#535353",
                            textAlign: "right",
                          }}
                        >
                          {student.ID}
                        </Col>
                        {editStatus === true && (
                          <Col xl={3} lg={3} md={3} sm={3} xs={3}>
                            <Form.Check
                              type="checkbox"
                              value={student.Name}
                              onChange={(e) => addStudentHandler(e)}
                            />
                          </Col>
                        )}
                      </Row>
                    </ListGroup.Item>
                  );
                })}
              </ListGroup>
            )}
          </Col>
        </Row>

        {editStatus === false && (
          <>
            <Row>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                    borderColor: "#007bff",
                  }}
                  onClick={() => setEditStatus(true)}
                >
                  Edit
                </Button>
              </Col>
            </Row>
            <Row>
              <Col>
                <CsvDownload
                  data={attendanceList}
                  filename={selectedDate + " " + selectedClass + ".csv"}
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                    borderRadius: "5px",
                    borderColor: "#007bff",
                    padding: ".375rem .75rem",
                  }}
                >
                  Download
                </CsvDownload>
              </Col>
            </Row>
          </>
        )}

        {editStatus === true && (
          <>
            <Row>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                    borderColor: "#007bff",
                  }}
                  onClick={() => {
                    setEditStatus(false);
                    saveHandler();
                  }}
                >
                  Save
                </Button>
              </Col>
            </Row>
            <Row>
              <Col>
                <Button
                  style={{
                    width: "100%",
                    backgroundColor: "white",
                    color: "#535353",
                    fontWeight: "bold",
                    marginTop: "10px",
                    borderColor: "#007bff",
                  }}
                  onClick={() => setEditStatus(false)}
                >
                  Cancel
                </Button>
              </Col>
            </Row>
          </>
        )}
      </Container>
    </>
  );
};

export default SelectedAttendance;
