import React, { useState } from "react";
import { Row, Col, Container, Spinner } from "react-bootstrap";

import Status from "../Authentication/Status";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(false);

  return (
    <>
      {isLoading === true ? (
        <>
          {setTimeout(() => {
            setIsLoading(true);
          }, 1500)}

          <Container className="text-center spinner" fluid>
            <Row>
              <Col>
                <Spinner
                  className="loader"
                  animation="border"
                  style={{ width: "5rem", height: "5rem", color: "#3241c9" }}
                />
                <h3>Loading...</h3>
              </Col>
            </Row>
          </Container>
        </>
      ) : (
        <>
          <Container className="text-center dashboard" fluid>
            <Row>
              <Col>
                <h1>Dashboard</h1>
              </Col>
            </Row>
            <Row>
              <Col>
                <Status />
              </Col>
            </Row>
          </Container>
        </>
      )}
    </>
  );
};

export default Dashboard;
