import React, { useState } from "react";
import axios from "axios";
import { Row, Col, Container, Button, Modal } from "react-bootstrap";
import SelectAttendance from "./SelectedAttendance";

const LatestAttendance = (attendance) => {
  const attendanceArray = attendance.attendance;

  const [showModal, setShowModal] = useState(false);
  const [classTitle, setClassTitle] = useState("");
  const [date, setDate] = useState("");

  const selectHandler = (e) => {
    setShowModal(true);
    setDate(e.currentTarget.value);
    setClassTitle(e.target.id);
    axios.post("http://localhost:3001/sheet/class", e.target.id);
  };

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          {attendanceArray.map((result) => {
            return (
              <Col xl={4} lg={4} md={4} sm={6} xs={6} key={result.Class}>
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "#535353",
                    marginBottom: "20px",
                    height: "15vh",
                    fontSize: "calc(1rem + 0.8vw)",
                    fontWeight: "bold",
                    borderRadius: "10px",
                  }}
                  id={result.Class}
                  value={result.Date}
                  onClick={(e) => selectHandler(e)}
                >
                  <div
                    style={{ marginBottom: "5px" }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => selectHandler(e)}
                  >
                    {result.Class}
                  </div>

                  <div
                    style={{
                      fontSize: "calc(1rem + 0.5vw)",
                      color: "white",
                      backgroundColor: "#3e4ccd",
                      borderRadius: "10px",
                      marginBottom: "5px",
                    }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => selectHandler(e)}
                  >
                    {result.Present} / {result.Total_Students}
                  </div>

                  <div
                    style={{ fontSize: "calc(1rem + 0.5vw)" }}
                    id={result.Class}
                    value={result.Date}
                    onClick={(e) => selectHandler(e)}
                  >
                    {result.Date}
                  </div>
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
      <Modal
        className="text-center"
        size="lg"
        show={showModal}
        onHide={() => {
          setShowModal(false);
        }}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <h3 style={{ fontWeight: "bold", color: "#535353" }}>
              {classTitle}
            </h3>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Row>
            <Col>
              <SelectAttendance />
            </Col>
          </Row>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default LatestAttendance;
