import React, { useState, useEffect } from "react";
import axios from "axios";
import { Row, Col, Container, Button } from "react-bootstrap";

const LatestAttendance = () => {
  const [data, setData] = useState([]);
  const [classData, setClassData] = useState([])

  const getData = async () => {
    /*await fetch("/aws/api")
      .then((res) => res.json())
      .then((res) => setData(res));*/

    await fetch("/drive/api")
      .then((res) => res.json())
      .then((res) => Object.assign({}, res.files[0]))
      .then((data) => {
        axios.post("/sheet/id", data);
      });

    await fetch("/sheet/api")
      .then((res) => res.json())
      .then((res) => setClassData(res.sheets));

    await fetch("/sheet/student")
      .then((res) => res.json())
      .then((res) => setData(res));
  };

  useEffect(() => {
    getData();
  }, []);

  console.log(data)
  
  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
        {classData.map((result) => {
            return (
              <Col
                xl={4}
                lg={4}
                md={4}
                sm={6}
                xs={6}
                key={result.properties.title}
              >
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "black",
                    marginBottom: "20px",
                    width: "100%",
                    height: "10vh",
                    fontSize: "calc(10px + 1.2vw)",
                    fontWeight: "bold",
                  }}
                  id={result.properties.title}
                  onClick={(e) => console.log(e.target.id)}
                >
                  {result.properties.title}
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
