import React, { useState, useContext } from "react";
import { AccountContext } from "./Account";

import { Row, Col, Container, Image } from "react-bootstrap";

import emailIcon from "../assets/email.png";
import passwordIcon from "../assets/password.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { authenticate } = useContext(AccountContext);

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        console.log("Logged in!", data);
      })
      .catch((err) => {
        console.error("Failed to login!", err);
      });
  };

  return (
    <div>
      <Container className="login text-center" fluid>
        <Row>
          <Col className="bg-blue" xl={6} lg={6} md={12} sm={12} xs={12}>
            <h3 style={{ fontSize: "calc(2rem + 2vw)", color: "white" }}>
              RASPITAR WEB
            </h3>
          </Col>
          <Col xl={6} lg={6} md={12} sm={12} xs={12}>
            <Row>
              <Col>
                <h3
                  style={{ fontSize: "calc(2rem + 2vw)", fontWeight: "bold" }}
                >
                  Hello there,
                </h3>
              </Col>
            </Row>
            <Row>
              <h3
                style={{
                  fontSize: "calc(1rem + 0.8vw)",
                  fontWeight: "normal",
                  color: "#848484",
                }}
              >
                Login to your account
              </h3>
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Login;

