import React from "react";
import { Row, Col, Container, Toast } from "react-bootstrap";

const StudentMatters = (temperatureMatter) => {
  const temperatureMatterArray = temperatureMatter.temperatureMatter;
  return (
    <Container className="student-matters text-center" fluid>
      <h3 style={{ color: "white", textAlign: "left" }}>Student Matters</h3>
      <Row style={{ marginBottom: "10px" }}>
        <Col>
          <h3 style={{ color: "white", fontSize: "22px", textAlign: "left" }}>
            High Temperature
          </h3>

          <Row style={{ marginBottom: "10px" }}>
            {temperatureMatterArray.map((result) => {
              return (
                <Col xl={4} lg={4} md={4} sm={6} xs={6} key={result.Name}>
                  <Toast>
                    <Toast.Header>
                      <strong className="mr-auto">{result.Name}</strong>
                      <small style={{ color: "black", fontWeight: "bold" }}>
                        {result.Class}
                      </small>
                    </Toast.Header>
                    <Toast.Body style={{ color: "black", fontWeight: "bold" }}>
                      {result.Temperature} Â°C
                    </Toast.Body>
                  </Toast>
                </Col>
              );
            })}
          </Row>
        </Col>

        <Col>
          <h3 style={{ color: "white", fontSize: "22px", textAlign: "left" }}>
            Low Attendance
          </h3>
        </Col>
      </Row>
    </Container>
  );
};

export default StudentMatters;
