import React, { useState, useContext } from "react";
import { AccountContext } from "./Account";

import { Row, Col, Container, Image } from "react-bootstrap";

import emailIcon from "../assets/email.png";
import passwordIcon from "../assets/password.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const { authenticate } = useContext(AccountContext);

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        console.log("Logged in!", data);
      })
      .catch((err) => {
        console.error("Failed to login!", err);
      });
  };

  return (
    <>
      <Container className="login" fluid>
        <Row className="bg-blue">
          <Col>
            <h3 style={{ fontSize: "calc(2rem + 2vw)" }}>RASPITAR WEB</h3>
          </Col>
        </Row>

        <div className="col-md-6 p-0 bg-white h-md-100 ">
          <div className=" align-items-center h-md-100 p-5 justify-content-center">
            <Row>
              <h3 style={{ fontSize: "calc(2rem + 2vw)", fontWeight: "bold" }}>
                Hello there,
              </h3>
            </Row>
            <Row>
              <h3
                style={{
                  fontSize: "calc(1rem + 0.8vw)",
                  fontWeight: "normal",
                  color: "#848484",
                }}
              >
                Login to your account
              </h3>
            </Row>
            <form onSubmit={onSubmit}>
              <Row>
                <Col>
                  <Image
                    src={emailIcon}
                    alt=""
                    fluid
                    style={{ width: "40px", height: "37px" }}
                  />
                  <input
                    value={email}
                    placeholder="Email"
                    onChange={(event) => setEmail(event.target.value)}
                  />
                </Col>
              </Row>

              <Row>
                <Col>
                  <Image
                    src={passwordIcon}
                    alt=""
                    fluid
                    style={{ width: "40px", height: "37px" }}
                  />
                  <input
                    value={password}
                    placeholder="Password"
                    onChange={(event) => setPassword(event.target.value)}
                  />
                </Col>
              </Row>

              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      </Container>
    </>
  );
};

export default Login;
