import React, { useContext, useState, useEffect } from "react";
import { AccountContext } from "../Authentication/Account";
import { Row, Col, Container, Jumbotron, Button } from "react-bootstrap";
import gapi from "gapi-client";
import "../../credentials.json";

const LatestAttendance = () => {
  const { userEmail } = useContext(AccountContext);
  const [data, setData] = useState([]);

  const getData = () => {
    var CLIENT_ID = "115460138632126755033";
    var API_KEY = "AIzaSyDFjwcJ8OuJP24_WCn-bxX1LsJMCSvBsl8";
    var DISCOVERY_DOCS = [
      "https://sheets.googleapis.com/$discovery/rest?version=v4",
    ];
    var SCOPES = "https://www.googleapis.com/auth/spreadsheets.readonly";

    const handleClientLoad = () => {
      gapi.load("client:auth2", initClient);
    };

    function initClient() {
      gapi.client
        .init({
          apiKey: API_KEY,
          clientId: CLIENT_ID,
          discoveryDocs: DISCOVERY_DOCS,
          scope: SCOPES,
        })
        .then(
          function () {
            // Listen for sign-in state changes.
            gapi.auth2.getAuthInstance().isSignedIn.listen(updateSigninStatus);

            // Handle the initial sign-in state.
            updateSigninStatus(gapi.auth2.getAuthInstance().isSignedIn.get());
            authorizeButton.onclick = handleAuthClick;
            signoutButton.onclick = handleSignoutClick;
          },
          function (error) {
            appendPre(JSON.stringify(error, null, 2));
          }
        );
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="latest-attendance text-center" fluid>
        <h3 style={{ color: "white", textAlign: "left" }}>Latest Attendance</h3>
        <Row>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
          <Col>
            <Jumbotron
              style={{
                backgroundColor: "white",
                width: "auto",
                height: "auto",
              }}
            >
              <h3>CE-D21</h3>
              <Button>19 / 24</Button>
            </Jumbotron>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default LatestAttendance;
