import React, { useState, useEffect } from "react";
import { Row, Col, Container, Button, Spinner } from "react-bootstrap";

const AttendanceList = (classTitle) => {
  const [attendanceList, setAttendanceList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const getData = async () => {
    let attendanceArray = [];

    await fetch("/aws/api") // Get Student Attendance
      .then((res) => res.json())
      .then((res) => {
        attendanceArray = res;
        console.log(attendanceArray);
        setIsLoading(false);
      });

    setAttendanceList(attendanceArray);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="text-center attendancelist">
        <Row>
          <Col>
            <h3
              style={{
                fontWeight: "bold",
                color: "white",
                textAlign: "left",
                marginTop: "10px",
                marginBottom: "10px",
              }}
            >
              Attendance List
            </h3>
          </Col>
        </Row>
        <Row>
          <Col>
            {isLoading ? (
              <Spinner animation="border" style={{ color: "white" }} />
            ) : (
              <h3>Loaded</h3>
            )}
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AttendanceList;
