import React, { useEffect, useState } from "react";
import axios from "axios";
import { Row, Col, Container, ListGroup, Spinner } from "react-bootstrap";

const StudentList = (classTitle) => {
  const [studentList, setStudentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const className = classTitle.classTitle;

  const getData = async () => {
    let studentArray = [];

    axios.post("http://localhost:3001/sheet/class", className);

    await fetch("/drive/api");

    await fetch("/sheet/student") // Get Student List
      .then((res) => res.json())
      .then((res) => (studentArray = res));

    console.log(studentArray);
    setStudentList(studentArray);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="text-center studentlist">
        <Row>
          <Col>
            <h3
              style={{
                fontWeight: "bold",
                color: "white",
                textAlign: "left",
                marginTop: "10px",
                marginBottom: "10px",
              }}
            >
              Student List
            </h3>
          </Col>
        </Row>
        <Row>
          <Col>
            <ListGroup>
              {studentList.map((student) => {
                return (
                  <ListGroup.Item key={student[1]}>
                    <Row>
                      <Col
                        xl={9}
                        lg={9}
                        md={9}
                        sm={9}
                        xs={9}
                        style={{
                          textAlign: "left",
                          fontWeight: "bold",
                          color: "#535353",
                        }}
                      >
                        {student[0]}
                      </Col>
                      <Col
                        xl={3}
                        lg={3}
                        md={3}
                        sm={3}
                        xs={3}
                        style={{
                          textAlign: "right",
                          fontWeight: "bold",
                          color: "#535353",
                        }}
                      >
                        {student[1]}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                );
              })}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default StudentList;
