import React, { useState, useEffect } from "react";
import { Row, Col, Container, ListGroup, Spinner } from "react-bootstrap";

const AttendanceList = () => {
  const [attendanceList, setAttendanceList] = useState([]);
  const [dates, setDates] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    let dateArray = [];

    const getData = async () => {
      const response = await fetch("/aws/api").then((res) => res.json()); // Get Student Attendance
      response.sort((a, b) => {
        return new Date(b.Date) - new Date(a.Date);
      });

      response.forEach((data) => {
        dateArray.push(data.Date.split(" ")[0]);
      });

      dateArray = [...new Set(dateArray)];

      if (mounted) {
        setAttendanceList(response);
        setDates(dateArray);
        setIsLoading(false);
      }
    };
    getData();
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <>
      <Container className="text-center attendancelist">
        <Row>
          <Col>
            <h3
              style={{
                fontWeight: "bold",
                color: "white",
                textAlign: "left",
                marginTop: "10px",
                marginBottom: "10px",
              }}
            >
              Attendance List
            </h3>
          </Col>
        </Row>
        <Row>
          <Col>
            {isLoading ? (
              <Spinner animation="border" style={{ color: "white" }} />
            ) : (
              <ListGroup>
                {dates.map((date) => {
                  return (
                    <ListGroup.Item
                      key={date}
                      action
                      style={{ fontWeight: "bold" }}
                    >
                      {date}
                    </ListGroup.Item>
                  );
                })}
              </ListGroup>
            )}
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AttendanceList;
