import React, { useEffect } from "react";
import { Row, Col, Container, Jumbotron } from "react-bootstrap";
import axios from "axios";

const StudentMatters = () => {
  const getData = async () => {
    let classArray = [];
    let studentArray = [];
    let dataArray = [];

    await fetch("/drive/api");

    await fetch("/sheet/api") // Get Class List
      .then((res) => res.json())
      .then((res) => {
        res.sheets.forEach((result) => {
          classArray.push(result.properties.title);
        });
      });

    for (const classTitle of classArray) {
      axios.post("http://localhost:3001/sheet/class", classTitle);

      await fetch("/sheet/student") // Get Student List
        .then((res) => res.json())
        .then((res) => (studentArray = res));

      await fetch("/aws/api") // Get Student Attendance
        .then((res) => res.json())
        .then((res) => {
          dataArray = res;
        });

      // Sort to latest date
      dataArray.sort((a, b) => {
        return new Date(b.Date) - new Date(a.Date);
      });
    }
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <Container className="student-matters text-center" fluid>
      <h3 style={{ color: "white", textAlign: "left" }}>Student Matters</h3>
      <Row>
        <Col>
          <Jumbotron
            style={{
              backgroundColor: "white",
              width: "auto",
              height: "auto",
            }}
          >
            <h3>High Temperature</h3>
          </Jumbotron>
        </Col>
      </Row>
      <Row>
        <Col>
          <Jumbotron
            style={{
              backgroundColor: "white",
              width: "auto",
              height: "auto",
            }}
          >
            <h3>Low Attendance</h3>
          </Jumbotron>
        </Col>
      </Row>
    </Container>
  );
};

export default StudentMatters;
