import React, { useEffect, useState } from "react";
import axios from "axios";
import { Row, Col, Container, Button } from "react-bootstrap";

const Classes = () => {
  const [classData, setClassData] = useState([])

  const getData = async () => {
    await fetch("/drive/api")
      .then((res) => res.json())
      .then((res) => console.log(res))

    await fetch("/drive/classDetails")

    await fetch("/sheet/api")
      .then((res) => res.json())
      .then((res) => setClassData(res.sheets));
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="classes text-center" fluid>
        <Row>
          <Col>
            <h3 style={{ color: "white", textAlign: "left" }}>Your Classes</h3>
          </Col>
        </Row>
        <Row>
          {classData.map((result) => {
            return (
              <Col
                xl={4}
                lg={4}
                md={4}
                sm={6}
                xs={6}
                key={result.properties.title}
              >
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "black",
                    marginBottom: "20px",
                    width: "100%",
                    height: "10vh",
                    fontSize: "calc(10px + 1.2vw)",
                    fontWeight: "bold",
                  }}
                  id={result.properties.title}
                  onClick={(e) => console.log(e.target.id)}
                >
                  {result.properties.title}
                </Button>
              </Col>
            );
          })}
        </Row>
      </Container>
    </>
  );
};

export default Classes;
