import React from "react";
import { Row, Col, Container, Button } from "react-bootstrap";

const StudentMatters = (temperatureMatter) => {
  const temperatureMatterArray = temperatureMatter.temperatureMatter;
  return (
    <Container className="student-matters text-center" fluid>
      <h3 style={{ color: "white", textAlign: "left" }}>Student Matters</h3>
      <Row>
        <Col>
          <h3>High Temperature</h3>

          {temperatureMatterArray.map((result) => {
            return (
              <Col xl={4} lg={4} md={4} sm={6} xs={6} key={result.Name}>
                <Button
                  size="lg"
                  block={true}
                  style={{
                    backgroundColor: "white",
                    color: "black",
                    marginBottom: "20px",
                    width: "100%",
                    height: "10vh",
                    fontSize: "calc(10px + 1.2vw)",
                    fontWeight: "bold",
                    borderRadius: "10px",
                  }}
                  id={result.Name}
                  onClick={(e) => console.log(e.target.id)}
                >
                  {result.Name}
                </Button>
              </Col>
            );
          })}
        </Col>
      </Row>
      <Row>
        <Col>
          <h3>Low Attendance</h3>
        </Col>
      </Row>
    </Container>
  );
};

export default StudentMatters;
