import React, { useEffect, useState } from "react";
import axios from "axios";
import { Row, Col, Container } from "react-bootstrap";

import NavBar from "./NavBar";
import LatestAttendance from "./LatestAttendance";
import StudentMatters from "./StudentMatters";
import Classes from "./Classes";

const Dashboard = () => {
  const [classes, setClasses] = useState([]);
  const [attendance, setAttendance] = useState([]);
  const [temperatureMatter, setTemperatureMatter] = useState([]);

  useEffect(() => {
    let classArray = [];
    let studentArray = [];
    let dataArray = [];
    let attendance = [];
    let temperatureMatter = [];
    let mounted = true;
    const getData = async () => {
      if (mounted) {
        await fetch("/drive/api");

        await fetch("/sheet/api")
          .then((res) => res.json())
          .then((res) => {
            setClasses(res.sheets); // Class Component
            res.sheets.forEach((result) => {
              classArray.push(result.properties.title);
            });
          });

        for (const classTitle of classArray) {
          axios.post("http://localhost:3001/sheet/class", classTitle);

          await fetch("/sheet/student") // Get Student List
            .then((res) => res.json())
            .then((res) => (studentArray = res));

          await fetch("/aws/api") // Get Student Attendance
            .then((res) => res.json())
            .then((res) => {
              dataArray = res;
            });

          // Sort to latest date
          dataArray.sort((a, b) => {
            return new Date(b.Date) - new Date(a.Date);
          });

          // Data formation
          const latestAttendanceData = dataArray.filter((res) =>
            res.Date.includes(dataArray[0].Date.split(" ")[0])
          );

          if (dataArray.length < 1 || dataArray === undefined) {
            // do nothing
          } else {
            const date = dataArray[0].Date.split(" ")[0];
            const totalStudents = studentArray.length;
            let presentCount = 0;

            studentArray.forEach((res) => {
              latestAttendanceData.forEach((student) => {
                if (res[0].includes(student.Name)) {
                  presentCount = presentCount + 1;
                }
              });
            });

            // Class, Date, Present, Total Students
            attendance.push({
              Class: classTitle,
              Date: date,
              Present: presentCount,
              Total_Students: totalStudents,
            });

            latestAttendanceData.forEach((student) => {
              if (student.Temperature >= 37.5) {
                temperatureMatter.push(student);
              }
            });
          }
        }
      }

      setAttendance(attendance); // LatestAttendance Component
      setTemperatureMatter(temperatureMatter);
    };
    getData();
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <>
      <NavBar />
      <Container className="text-center dashboard" fluid>
        <Row>
          <Col>
            <h3
              style={{
                fontSize: "calc(22px + 2vw)",
                fontWeight: "bold",
                color: "#535353",
                textAlign: "left",
              }}
            >
              Your Dashboard
            </h3>
          </Col>
        </Row>

        <Row>
          <Col xl={6} lg={12} md={12} sm={12} xs={12}>
            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <LatestAttendance attendance={attendance} />
              </Col>
            </Row>

            <Row style={{ marginBottom: "10px" }}>
              <Col xl={12} lg={12} md={12} sm={12} xs={12}>
                <StudentMatters temperatureMatter={temperatureMatter} />
              </Col>
            </Row>
          </Col>

          <Col
            xl={6}
            lg={12}
            md={12}
            sm={12}
            xs={12}
            style={{ marginBottom: "10px" }}
          >
            <Classes classes={classes} />
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
