import React, { useState, useEffect } from "react";
import { Row, Col, Container, Spinner, ListGroup } from "react-bootstrap";
import CsvDownload from "react-json-to-csv";

const SelectedAttendance = (date) => {
  const selectedDate = date.date;

  const [isLoading, setIsLoading] = useState(true);
  const [attendanceList, setAttendanceList] = useState([]);
  const [selectedClass, setSelectedClass] = useState("");

  useEffect(() => {
    let mounted = true;

    const getData = async () => {
      const response = await fetch(
        "http://localhost:3001/aws/api"
      ).then((res) => res.json()); // Get Student Attendance

      response.sort((a, b) => {
        // Sort to latest datex
        return new Date(b.Date) - new Date(a.Date);
      });

      const attendance = response.filter((
        res // Filter Attendance By Selected Date
      ) => res.Date.includes(selectedDate));

      if (mounted) {
        setSelectedClass(attendance[0].Class);
        setAttendanceList(attendance);
        setIsLoading(false);
      }
    };
    getData();
    return () => {
      mounted = false;
    };
  }, [selectedDate]);

  return (
    <>
      <Container className="selected-attendance text-center" fluid>
        <Row>
          <Col>
            <h3
              style={{ fontWeight: "bold", color: "white", textAlign: "left" }}
            >
              {selectedDate}
            </h3>
          </Col>
        </Row>
        <Row>
          <Col>
            {isLoading ? (
              <Spinner animation="border" style={{ color: "white" }} />
            ) : (
              <ListGroup>
                {attendanceList.map((student) => {
                  return (
                    <ListGroup.Item key={student.Name}>
                      <Row>
                        <Col
                          xl={8}
                          lg={8}
                          md={8}
                          sm={8}
                          xs={8}
                          style={{
                            fontWeight: "bold",
                            color: "#535353",
                            textAlign: "left",
                          }}
                        >
                          {student.Name}
                        </Col>
                        <Col
                          xl={4}
                          lg={4}
                          md={4}
                          sm={4}
                          xs={4}
                          style={{
                            fontWeight: "bold",
                            color: "#535353",
                            textAlign: "right",
                          }}
                        >
                          {student.Temperature} Â°C
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  );
                })}
              </ListGroup>
            )}
          </Col>
        </Row>
        <Row>
          <Col>
            <CsvDownload
              data={attendanceList}
              filename={selectedDate + " " + selectedClass + ".csv"}
              style={{
                width: "100%",
                backgroundColor: "white",
                color: "#535353",
                fontWeight: "bold",
                marginTop: "10px",
                borderRadius: "5px",
                borderColor: "#007bff",
                padding: ".375rem .75rem",
              }}
            >
              Download
            </CsvDownload>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default SelectedAttendance;
