import React, { useState, useEffect } from "react";
import { Row, Col, Container, Button } from "react-bootstrap";

const AttendanceList = () => {
  const getData = async () => {
    let studentArray = [];

    axios.post("http://localhost:3001/sheet/class", className);

    await fetch("/drive/api");

    await fetch("/sheet/student") // Get Student List
      .then((res) => res.json())
      .then((res) => {
        studentArray = res;
        setIsLoading(false);
      });

    setStudentList(studentArray);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Container className="text-center">
        <Row>
          <Col>
            <h3
              style={{
                fontWeight: "bold",
                color: "#535353",
                textAlign: "left",
                marginTop: "10px",
                marginBottom: "10px",
              }}
            >
              Attendance List
            </h3>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AttendanceList;
